mapper.select:
SELECT * from picker_db.post
WHERE is_deleted = 0
ORDER BY updated_at DESC;

SELECT * from picker_db.post
WHERE is_deleted = 0
AND post_id = #{postId}
ORDER BY updated_at DESC;

SELECT * from picker_db.post
WHERE user_id = #{userId}
AND is_deleted = 0
ORDER BY updated_at DESC;

mapper.insert:
INSERT INTO picker_db.post
(user_id, post_text, temp_id)
VALUES(#{userId}, #{postText}, #{tempId});

mapper.update:
UPDATE picker_db.post
SET post_text=#{postText},
    temp_id=#{tempId}
WHERE post_id=#{postId}
AND user_id=#{userId}
AND is_deleted = 0;

mapper.delete:
UPDATE picker_db.post
SET is_deleted=1,
    temp_id=#{tempId}
WHERE post_id=#{postId}
AND user_id=#{userId}

mapper.update:
UPDATE picker_db.post
SET view_count = view_count + #{item.viewCount}
WHERE post_id = #{item.postId}
